mapper.select:
select sh.id ,sh.orderNo ,sh.name ,sh.phone ,sh.address ,sh.quan ,sh.total ,sh.addTime ,sh.status ,sh.returnTime ,sh.remark_sj ,sh.delivery_status ,sh.order_status ,c.type ,c.name comm_name from shipment sh left join stock s on s.id = sh.stock_id left join comm c on s.comm_id = c.id
sh.orderNo like '%'+#{orderNo}+'%'
sh.name like '%'+#{name}+'%'
sh.delivery_status = #{delivery_status}
sh.addTime between #{s_time} and #{e_time}
order by sh.addTime desc
